export default function scrollPin(){const t=document.querySelectorAll(".investor-protection__block_inner"),e=t.length;let o=[];t.forEach((n,i)=>{if(e!=i+1){let e,r;n.offsetHeight>window.innerHeight?(e="bottom bottom",r=n.offsetHeight+"px top",t[i+1].parentElement.style.setProperty("--height",`-${window.innerHeight}px`)):(t[i+1].parentElement.style.setProperty("--height",`-${n.offsetHeight+5}px`),e="top top",r=n.offsetHeight+"px top");const p=gsap.timeline({scrollTrigger:{trigger:n,start:e,end:r,pin:!0,scrub:!0}});p.pause(),o.push(p)}})}